import React, { useState } from 'react';
import CodeMirror from '@uiw/react-codemirror';
import { javascript } from '@codemirror/lang-javascript';
import './CodeReviewForm.css';

const CodeReviewForm: React.FC = () => {
  const [code, setCode] = useState<string>('');
  const [feedback, setFeedback] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!code.trim()) {
      setError('Please enter some code for analysis.');
      return;
    }

    setLoading(true);
    setError('');
    setFeedback('');

    try {
      const response = await fetch('http://localhost:5003/api/review', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code }),
      });
      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Server error');
      }
      const data = await response.json();
      setFeedback(data.feedback);
    } catch (err: any) {
      setError(err.message || 'An error occurred while submitting the request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-container">
      <h2>AI Code Reviewer</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="codeInput">Enter your code:</label>
        <CodeMirror
          value={code}
          height="200px"
          extensions={[javascript()]}
          onChange={(value) => setCode(value)}
          placeholder="e.g., function test() { return true; }"
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Please wait...' : 'Submit'}
        </button>
      </form>
      {error && <div className="error">Error: {error}</div>}
      {feedback && (
        <div className="feedback">
          <h3>Review Result:</h3>
          <pre>{feedback}</pre>
        </div>
      )}
    </div>
  );
};

export default CodeReviewForm;
