import React, { useState } from 'react';
import { 
  Box, 
  Button, 
  CircularProgress, 
  Container, 
  Paper, 
  Typography 
} from '@mui/material';
import CodeMirror from '@uiw/react-codemirror';
import { javascript } from '@codemirror/lang-javascript';

const CodeReviewForm: React.FC = () => {
  const [code, setCode] = useState<string>('');
  const [feedback, setFeedback] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!code.trim()) {
      setError('Please enter some code for analysis.');
      return;
    }

    setLoading(true);
    setError('');
    setFeedback('');

    try {
      const response = await fetch('http://localhost:5003/api/review', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code }),
      });
      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Server error');
      }
      const data = await response.json();
      setFeedback(data.feedback);
    } catch (err: any) {
      setError(err.message || 'An error occurred while submitting the request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ padding: 4, mt: 4 }}>
        <Typography variant="h4" component="h2" align="center" gutterBottom>
          AI Code Reviewer
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
          <Typography variant="subtitle1" gutterBottom>
            Enter your code:
          </Typography>
          <Box sx={{ mb: 2 }}>
            <CodeMirror
              value={code}
              height="200px"
              extensions={[javascript()]}
              onChange={(value) => setCode(value)}
              placeholder="e.g., function test() { return true; }"
            />
          </Box>
          <Box sx={{ position: 'relative', display: 'inline-flex' }}>
            <Button type="submit" variant="contained" disabled={loading}>
              {loading ? 'Please wait...' : 'Submit'}
            </Button>
            {loading && (
              <CircularProgress
                size={24}
                sx={{
                  color: 'primary.main',
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  marginTop: '-12px',
                  marginLeft: '-12px'
                }}
              />
            )}
          </Box>
        </Box>
        {error && (
          <Typography variant="body1" color="error" sx={{ mt: 2 }}>
            Error: {error}
          </Typography>
        )}
        {feedback && (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              Review Result:
            </Typography>
            <Paper variant="outlined" sx={{ padding: 2, backgroundColor: '#f4f4f4' }}>
              <pre>{feedback}</pre>
            </Paper>
          </Box>
        )}
      </Paper>
    </Container>
  );
};

export default CodeReviewForm;
